INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (1, 'Algemeen', 'Hoe werkt Datevrij.nl?', 'Datevrij.nl is een zeer eenvoudige datingsite waar je makkelijk andere mensen leert kennen voor een seksafspraakje. Het maakt niet uit of je op zoek bent naar een eenmalige date, casual sexdating of een (casual) relatie. Nadat je je gratis hebt aangemeld, kun je direct je profiel aanvullen en chatten met leuke personen. Uiteraard alleen als ze aan jouw wensen/eisen voldoen.', 1, '2019-07-14 18:30:56', '2019-10-11 11:53:54', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (2, 'Veiligheid', 'Is chatten op Datevrij.nl veilig?', 'Chatten via Datevrij.nl verloopt via een beveiligde (SSL) verbinding. Hierdoor ben je er zeker van dat niemand jouw gegevens kan inzien, laat staan stelen. Het is natuurlijk wel aan te raden om voorzichtig te zijn met het uitwisselen van persoonlijke gegevens.', 1, '2019-10-11 12:01:01', '2019-10-11 12:01:01', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (3, 'Credits', 'Wat zijn credits?', 'Credits gebruik je om berichten te versturen. Elk berichtje dat je stuurt kost één credit.', 1, '2019-10-11 12:06:19', '2019-10-11 12:06:19', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (4, 'Credits', 'Waarom moet ik betalen voor credits?', 'Door credits te gebruiken voor het versturen van berichten, zit je niet aan een abonnement of duur lidmaatschap vast. Je hebt geheel zelf in de hand hoeveel je uitgeeft. Bovendien stelt het ons in staat om de website te allen tijde te blijven verbeteren en bijvoorbeeld profielen en/of gerapporteerde profielteksten/foto''s snel en adequaat te kunnen controleren.', 1, '2019-10-11 12:11:08', '2019-10-11 12:11:08', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (5, 'Veiligheid', 'Wat is een goed wachtwoord?', 'Een goed wachtwoord is een veilig wachtwoord. Kies geen eenvoudige combinaties, maar eerder voor een unieke, niet eerder gebruikte combinatie. Zo voorkom je dat iemand je wachtwoord kan raden.', 1, '2019-10-11 12:12:41', '2019-10-11 12:12:41', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (6, 'Credits', 'Hoe koop ik credits?', 'Door op "Koop Credits" te klikken kun je snel en eenvoudig credits aanschaffen. Let op: hoe groter de bundel, des te goedkoper de credits!', 1, '2019-10-11 16:54:04', '2019-10-11 16:54:04', 0);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (7, 'Profiel', 'Hoe bekijk ik mijn eigen profiel?', 'Je kunt je profiel bekijken door rechts bovenin op je afbeelding te klikken en vervolgens op "Profiel Bekijken"', 1, '2019-11-14 13:07:28', '2019-11-14 13:07:28', 1);
INSERT INTO dating.faqs (id, section, title, body, status, created_at, updated_at, priority) VALUES (8, 'Profiel', 'Hoe kan ik mijn persoonlijke profiel aanpassen?', 'Wanneer je rechts bovenin op "Profiel Bekijken" klikt, kun je je profielgegevens wijzigen. Mocht je er zelf niet uitkomen, stuur dan even een mail naar info@datevrij.nl', 1, '2019-11-14 13:08:25', '2019-11-14 13:08:25', 2);